// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";

interface IStargateRouterMock {
    struct lzTxObj {
        uint256 dstGasForCall;
        uint256 dstNativeAmount;
        bytes dstNativeAddr;
    }

    function swap(
        uint16 _dstChainId,
        uint256 _srcPoolId,
        uint256 _dstPoolId,
        address payable _refundAddress,
        uint256 _amountLD,
        uint256 _minAmountLD,
        lzTxObj memory _lzTxParams,
        bytes calldata _to,
        bytes calldata _payload
    ) external payable;
}

contract StargateRouterMock is IStargateRouterMock {
    IERC20 public token;

    constructor(IERC20 _token) {
        token = _token;
    }

    function swap(
        uint16,
        uint256,
        uint256,
        address payable _refundAddress,
        uint256 _amountLD,
        uint256,
        IStargateRouterMock.lzTxObj memory,
        bytes calldata _to,
        bytes calldata
    ) external payable override {
        require(_amountLD > 0, "Stargate: cannot swap 0");
        require(
            _refundAddress != address(0x0),
            "Stargate: _refundAddress cannot be 0x0"
        );
        bytes32 converted = bytes32(_to[:32]);
        address tempAddress = address(uint160(uint256(converted)));

        token.transferFrom(msg.sender, address(this), _amountLD);
        token.transfer(tempAddress, _amountLD);
    }
}

contracts/YieldBoxMock.sol.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.18;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";

contract YieldBoxMock {
    mapping(address => uint256) public balances;
    mapping(uint256 => address) public assets;

    function addAsset(uint256 assetId, address asset) external {
        assets[assetId] = asset;
    }

    function depositAsset(
        uint256 assetId,
        address from,
        address to,
        uint256 amount,
        uint256
    ) external returns (uint256 amountOut, uint256 shareOut) {
        require(
            ERC20(assets[assetId]).transferFrom(from, address(this), amount),
            "failed transfer"
        );
        balances[to] += amount;
        return (amount, amount);
    }

    function withdraw(
        uint256 assetId,
        address from,
        address to,
        uint256 amount,
        uint256
    ) external returns (uint256 amountOut, uint256 shareOut) {
        require(balances[from] >= amount, "not enough");
        ERC20(assets[assetId]).transfer(to, amount);
        balances[from] -= amount;
        return (amount, amount);
    }
}

